#!/usr/bin/env bash
# Profile: brave-kenp
# Generated by Semsumo v7.0.0
set -e

echo "Initializing brave-kenp..."

# Switch to workspace
if [[ "1" != "0" ]] && command -v hyprctl >/dev/null 2>&1; then
    CURRENT_WORKSPACE=$(hyprctl activeworkspace -j | grep -o '"id": [0-9]*' | grep -o '[0-9]*' || echo "")
    
    if [[ "$CURRENT_WORKSPACE" != "1" ]]; then
        echo "Switching to workspace 1..."
        hyprctl dispatch workspace "1"
        sleep 1
    else
        echo "Already on workspace 1, skipping switch."
    fi
fi

echo "Starting application..."
echo "COMMAND: profile_brave Kenp --class Kenp --title Kenp --restore-last-session"
echo "VPN MODE: secure"

# Start application with VPN mode
case "secure" in
    bypass)
        if command -v mullvad >/dev/null 2>&1 && mullvad status 2>/dev/null | grep -q "Connected"; then
            if command -v mullvad-exclude >/dev/null 2>&1; then
                echo "Starting with VPN bypass (mullvad-exclude)"
                mullvad-exclude profile_brave Kenp --class Kenp --title Kenp --restore-last-session &
            else
                echo "WARNING: mullvad-exclude not found, starting normally"
                profile_brave Kenp --class Kenp --title Kenp --restore-last-session &
            fi
        else
            echo "VPN not connected, starting normally"
            profile_brave Kenp --class Kenp --title Kenp --restore-last-session &
        fi
        ;;
    secure|*)
        if command -v mullvad >/dev/null 2>&1 && mullvad status 2>/dev/null | grep -q "Connected"; then
            echo "Starting with VPN protection"
        else
            echo "WARNING: VPN not connected! Starting without protection"
        fi
        profile_brave Kenp --class Kenp --title Kenp --restore-last-session &
        ;;
esac

# Save PID
APP_PID=$!
mkdir -p "/tmp/semsumo"
echo "$APP_PID" > "/tmp/semsumo/brave-kenp.pid"
echo "Application started (PID: $APP_PID)"

# Make fullscreen if needed
if [[ "false" == "true" ]]; then
    echo "Waiting 2 seconds for application to load..."
    sleep 2
    
    if command -v hyprctl >/dev/null 2>&1; then
        echo "Making fullscreen..."
        hyprctl dispatch fullscreen 1
    fi
fi

exit 0
